import React from "react";

const Card = ({ recipe }) => {
  return (
    <div className="tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
      <h3>{recipe.name}</h3>
      <p>
        {recipe.type} - {recipe.author}
      </p>
      <hr />
    </div>
  );
};

export default Card;
