import React, { useState, useEffect } from "react";
import axios from "./mock/mockAxios";
import "style.css";

import CardList from "./components/CardList/CardList";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    (async function fetchRecipes() {
      const { data } = await axios.get("/recipes");
      setRecipes(data);
    })();
  }, []);

  const filteredRecipes = () => {
    return recipes.filter(recipe => {
      return recipe.name.toLowerCase().includes(searchTerm.toLowerCase());
    });
  };

  return (
    <div className="recipe f1 tc">
      <h1 className="recipe f1 tc">Recipes</h1>

      <input className="recipe" onChange={e => setSearchTerm(e.target.value)} value={searchTerm} />

      <div className="recipe">
        <CardList recipes={filteredRecipes()} />
      </div>
    </div>
  );
}

export default App;
